package com.example.antriankesehatan.alarmManagerimport android.app.NotificationManagerimport android.app.PendingIntentimport android.content.BroadcastReceiverimport android.content.Contextimport android.content.Context.NOTIFICATION_SERVICEimport android.content.Intentimport androidx.core.app.NotificationCompatimport com.example.antriankesehatan.MainActivityimport com.example.antriankesehatan.Rclass AlarmReceiver: BroadcastReceiver() {    companion object {        const val NOTIFICATION_ID = 100    }    override fun onReceive(context: Context?, intent: Intent?) {        if (context != null) {            deliverNotification(context)        }    }    private fun deliverNotification(context: Context){        val notificationManager = context.getSystemService(NOTIFICATION_SERVICE) as NotificationManager        val intent = Intent(context, MainActivity::class.java)        val pendingIntent = PendingIntent.getActivity(context, NOTIFICATION_ID, intent, PendingIntent.FLAG_UPDATE_CURRENT)        val builder = NotificationCompat.Builder(context)            .setSmallIcon(R.drawable.ic_doctor)            .setContentTitle("Pengingat Antrian")            .setContentText("Sebentar lagi giliran anda untuk periksa :) ")            .setContentIntent(pendingIntent)            .setPriority(NotificationCompat.PRIORITY_HIGH)            .setDefaults(NotificationCompat.DEFAULT_ALL)            .setAutoCancel(true)        notificationManager.notify(NOTIFICATION_ID, builder.build())    }}