package com.example.antriankesehatan.alarmManagerimport android.app.NotificationChannelimport android.app.NotificationManagerimport android.content.Contextimport android.os.Buildimport androidx.core.app.NotificationCompatimport androidx.work.Workerimport androidx.work.WorkerParametersimport com.example.antriankesehatan.R// Local Versionclass AlarmWorker(context: Context, workerParameters: WorkerParameters) :    Worker(context, workerParameters) {    override fun doWork(): Result {        createNotification()        return Result.success()    }    private fun createNotification(){        val title = "Pengingat Antrian"        val description = "Sebentar lagi giliran anda untuk periksa kesehatan"        val notificationManager =            applicationContext.getSystemService(Context.NOTIFICATION_SERVICE) as NotificationManager        val notification: NotificationCompat.Builder =            NotificationCompat.Builder(applicationContext, MyWorker.CHANNEL_ID)                .setSmallIcon(R.drawable.ic_doctor)                .setContentTitle(title)                .setContentText(description)                .setAutoCancel(true)                .setPriority(NotificationCompat.PRIORITY_HIGH)                .setDefaults(NotificationCompat.DEFAULT_ALL)        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.O) {            val channel =                NotificationChannel(MyWorker.CHANNEL_ID,                    MyWorker.CHANNEL_NAME,                    NotificationManager.IMPORTANCE_HIGH)            notification.setChannelId(MyWorker.CHANNEL_ID)            notificationManager.createNotificationChannel(channel)        }        notificationManager.notify(MyWorker.NOTIFICATION_ID, notification.build())    }}