package com.example.antriankesehatan.pusherimport android.content.Contextimport android.content.Intentimport android.util.Logimport com.example.antriankesehatan.MainActivityimport com.pusher.client.Pusherimport com.pusher.client.PusherOptionsobject NotificationPusher {    private const val APP_CLUSTER = "mt1"    private const val APP_KEY = "1045a4cd84dc5ec8c88b"    private const val APP_SECRET = "d8e4589eaf0bfd8c1f5b"    private const val APP_ID = "1432716"    data class Message(        val title: String = "",        val desc: String = "",    )    fun setUpPusher(context: Context) {        val option = PusherOptions()        option.setCluster(APP_CLUSTER)        val pusher = Pusher(APP_KEY, option)        pusher.connect()        val channel = pusher.subscribe("broadcast")        channel.bind("berita-info") { event ->            Log.d("EVENT_PUSHER", event.data + " : " + event.eventName + " : " + event.channelName)            val intent = Intent(context, MainActivity::class.java)            val custom = NotificationCustom()          //  custom.sendMessageNotification(intent, context, "Antrian Kesehatan", event.data)            Message("Antrian Kesehatan", event.data)        }    }}