package com.example.antriankesehatan.pusherimport android.app.Serviceimport android.content.Intentimport android.os.IBinderimport android.widget.Toastclass ServiceNotification : Service() {    override fun onCreate() {        super.onCreate()        NotificationPusher.setUpPusher(applicationContext)    }    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {        Toast.makeText(applicationContext, "Service Running", Toast.LENGTH_SHORT).show()        NotificationPusher.setUpPusher(applicationContext)        return START_STICKY    }    override fun onBind(intent: Intent?): IBinder? {        return null    }}