package com.example.antriankesehatan.ui.antrianimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.antriankesehatan.databinding.ItemViewNomorAntrianBindingimport com.example.antriankesehatan.model.DataItemAntrianclass AntrianAdapter(private val listData: List<DataItemAntrian>) :    RecyclerView.Adapter<AntrianAdapter.AntrianViewHolder>() {    inner class AntrianViewHolder(val binding: ItemViewNomorAntrianBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(itemAntrian: DataItemAntrian) {            binding.apply {                tvDoctor.text = itemAntrian.dokter.namaDokter                tvAntrian.text = itemAntrian.antrian                tvDate.text = itemAntrian.tanggalPendaftaran                tvTime.text = itemAntrian.shiff            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): AntrianViewHolder {        return AntrianViewHolder(ItemViewNomorAntrianBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: AntrianViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int {        return listData.size    }}