package com.example.antriankesehatan.ui.doctorimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.antriankesehatan.databinding.ItemViewDokterBindingimport com.example.antriankesehatan.model.DataItemDokterclass DoctorAdapter(private val listData: List<DataItemDokter>) :    RecyclerView.Adapter<DoctorAdapter.DoctorViewHolder>() {    inner class DoctorViewHolder(private val binding: ItemViewDokterBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(doctor: DataItemDokter) {            binding.apply {                doctorName.text = doctor.namaDokter                doctorField.text = doctor.bidangDokter            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): DoctorViewHolder {        return DoctorViewHolder(ItemViewDokterBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: DoctorViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int {        return listData.size    }}