package com.example.antriankesehatan.ui.doctorimport android.os.Bundleimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.navigation.findNavControllerimport androidx.recyclerview.widget.RecyclerViewimport com.example.antriankesehatan.Rimport com.example.antriankesehatan.databinding.ItemViewDokterBindingimport com.example.antriankesehatan.model.DataItemDokterimport com.example.antriankesehatan.model.HariPraktekItemimport com.example.antriankesehatan.model.JamPraktekItemimport com.example.antriankesehatan.utils.Helperimport com.example.antriankesehatan.utils.loadImageCircleclass DoctorAdapter(private val listData: List<DataItemDokter>) :    RecyclerView.Adapter<DoctorAdapter.DoctorViewHolder>() {    private var praktekID = ""    inner class DoctorViewHolder(private val binding: ItemViewDokterBinding) :        RecyclerView.ViewHolder(binding.root) {        private var dataListHari = arrayListOf<HariPraktekItem>()        private var jamPraktekItem = arrayListOf<JamPraktekItem>()        fun bind(doctor: DataItemDokter) {            binding.apply {                doctorName.text = doctor.namaDokter                doctorField.text = doctor.bidangDokter                cvPhotoDoctor.let {                    Log.d("Data-Photo", doctor.photoDokter)                    itemView.context.loadImageCircle(Helper.BASE_IMAGE_URL_DOKTER + doctor.photoDokter,                        binding.cvPhotoDoctor)                }            }            for (praktek in doctor.hariPraktek) {                dataListHari.addAll(                    listOf(HariPraktekItem(                        praktek.updatedAt,                        praktek.dokterId,                        praktek.createdAt,                        praktek.jamPraktek,                        praktek.id,                        praktek.hariPraktek                    ))                )                jamPraktekItem.addAll(praktek.jamPraktek)                for (id in praktek.jamPraktek){                    praktekID = id.id.toString()                }            }            itemView.setOnClickListener {                val bundle = Bundle()                bundle.putString("DATA_ID_DOCTOR", doctor.id.toString())                bundle.putString("DATA_PRAKTEK_ID", praktekID)                bundle.putString("DATA_BIDANG_DOCTOR", doctor.bidangDokter)                bundle.putString("DATA_NAMA_DOCTOR", doctor.namaDokter)                bundle.putString("DATA_PHOTO_DOCTOR", doctor.photoDokter)                bundle.putSerializable("DATA_HARI_PRAKTEK_DOCTOR", dataListHari)                bundle.putSerializable("DATA_PRAKTEK", jamPraktekItem)                itemView.findNavController()                    .navigate(R.id.action_doctorFragment_to_jadwalFragment, bundle)            }        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): DoctorViewHolder {        return DoctorViewHolder(ItemViewDokterBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: DoctorViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int {        return listData.size    }}