package com.example.antriankesehatan.ui.homeimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.antriankesehatan.databinding.ItemViewAntrianBindingimport com.example.antriankesehatan.model.DataItemCurrentAntrianclass CurrentAntrianAdapter(private val listData: List<DataItemCurrentAntrian?>) :    RecyclerView.Adapter<CurrentAntrianAdapter.CurrentAntrianViewHolder>() {    inner class CurrentAntrianViewHolder(private val binding: ItemViewAntrianBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(item: DataItemCurrentAntrian) {            binding.apply {                tvDoctorName.text = item.dokter?.namaDokter                tvAntrianSaatIni.text = item.antrian            }        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int,    ): CurrentAntrianAdapter.CurrentAntrianViewHolder {        return CurrentAntrianViewHolder(ItemViewAntrianBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(        holder: CurrentAntrianAdapter.CurrentAntrianViewHolder,        position: Int,    ) {        listData[position]?.let { holder.bind(it) }    }    override fun getItemCount(): Int {        return listData.size    }}