package com.example.antriankesehatan.ui.riwayatimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.example.antriankesehatan.databinding.ItemViewRiwayatBindingimport com.example.antriankesehatan.model.DataItemRiwayatclass RiwayatAdapter(private val listData: List<DataItemRiwayat>) :    RecyclerView.Adapter<RiwayatAdapter.RiwayatViewHolder>() {    inner class RiwayatViewHolder(val binding: ItemViewRiwayatBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(itemRiwayat: DataItemRiwayat) {            binding.apply {                tvDate.text = itemRiwayat.tanggalPendaftaran                tvDoctor.text = itemRiwayat.dokter.namaDokter                tvKeluhan.text = itemRiwayat.keluhan                tvTime.text = itemRiwayat.jamPraktek.jamPraktek            }        }    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int,    ): RiwayatAdapter.RiwayatViewHolder {        return RiwayatViewHolder(ItemViewRiwayatBinding.inflate(LayoutInflater.from(parent.context),            parent,            false))    }    override fun onBindViewHolder(holder: RiwayatAdapter.RiwayatViewHolder, position: Int) {        holder.bind(listData[position])    }    override fun getItemCount(): Int {        return listData.size    }}