package com.example.antriankesehatan.utilsimport android.content.Contextclass SharedPreference(context: Context) {    companion object {        private const val PREF_NAME = "com.example.antriankesehatan.shared.preference"        private const val BEARER_TOKEN = "bearer_token"        private const val IS_LOGIN = "is_login"        private const val IMAGE_URL = "image_url"    }    private val preference = context.getSharedPreferences(PREF_NAME, Context.MODE_PRIVATE)    private val editor = preference.edit()    fun saveToken(token: String) {        editor.apply {            putString(BEARER_TOKEN, token)            apply()        }    }    fun saveLogin(isLogin: Boolean) {        editor.apply {            putBoolean(IS_LOGIN, isLogin)            apply()        }    }    fun saveImage(image: String) {        editor.apply {            putString(IMAGE_URL, image)            apply()        }    }    /** ====================================     *  GET VALUE FROM SHARED PREFERENCE     *  ====================================     */    fun getToken(): String {        return preference.getString(BEARER_TOKEN, "").toString()    }    fun getLogin(): Boolean {        return preference.getBoolean(IS_LOGIN, false)    }    fun getImage(): String? {        return preference.getString(IMAGE_URL, "")    }    /** ====================================     *  REMOVE LOGGING FROM SHARED PREFERENCE     *  ====================================     */    fun clear() {        editor.apply {            remove(BEARER_TOKEN)            remove(IS_LOGIN)            remove(IMAGE_URL)            apply()        }    }}